describe("StringCalculator",function(){it("Given an empty string should return zero",function(){var n=new StringCalculator;expect(n.Add("")).toEqual(0)}),it("given a single number should return number",function(){var n=new StringCalculator;expect(n.Add("1")).toEqual(1)}),it("given two numbers should return the sum of both numbers",function(){var n=new StringCalculator;expect(n.Add("1,2")).toEqual(3)}),it("given new line as delimitter should return the sum of all numbers",function(){var n=new StringCalculator;expect(n.Add("1\n2\n3")).toEqual(6)}),it("Negative numbers error message should contain negative number",function(){var n=new StringCalculator,t=new Error("Negative numbers not allowed (-1)");expect(function(){return n.Add("-1")}).toThrow(t)}),it("Numbers bigger than 1000 should be ignored",function(){var n=new StringCalculator;expect(n.Add("1,1001,2,3")).toEqual(6)})})